import firebase from 'firebase/app';
const admin = require('firebase-admin');
const functions = require('firebase-functions');

admin.initializeApp(functions.config().firebase);

let db = admin.firestore();

async function doCreateUserWithEmailAndPassword(email, password, firstName , lastName) {
	await firebase.auth().createUserWithEmailAndPassword(email, password).then(function(user){
		console.log('uid',user.uid)
		let data = {
			firstName: firstName,
			lastName : lastName,
			email : email
		  };
		  let setDoc = db.collection('users').doc(user.uid).set(data);	
		  console.log(setDoc)	  
	  }).catch(function(error) {
	  });
	firebase.auth().currentUser.updateProfile({ displayName: firstName+" "+lastName });
}

async function doChangePassword(email, oldPassword, newPassword) {
	let credential = firebase.auth.EmailAuthProvider.credential(email, oldPassword);
	await firebase.auth().currentUser.reauthenticateWithCredential(credential);
	await firebase.auth().currentUser.updatePassword(newPassword);
	await doSignOut();
}

async function doSignInWithEmailAndPassword(email, password) {
	await firebase.auth().signInWithEmailAndPassword(email, password);
}

async function doSocialSignIn(provider) {
	let socialProvider = null;
	if (provider === 'google') {
		socialProvider = new firebase.auth.GoogleAuthProvider();
	} else if (provider === 'facebook') {
		socialProvider = new firebase.auth.FacebookAuthProvider();
	}
	await firebase.auth().signInWithPopup(socialProvider);
}

async function doPasswordReset(email) {
	await firebase.auth().sendPasswordResetEmail(email);
}

async function doPasswordUpdate(password) {
	await firebase.auth().updatePassword(password);
}

async function doSignOut() {
	await firebase.auth().signOut();
}

export {
	doCreateUserWithEmailAndPassword,
	doSocialSignIn,
	doSignInWithEmailAndPassword,
	doPasswordReset,
	doPasswordUpdate,
	doSignOut,
	doChangePassword
};
